import React, {Component} from 'react';
import './../../styles/newsList.css';

class News extends Component{

    constructor(props) {
        super(props);
        this.state = {
            section: 'all',
            page: 5,
            Result : [],
            isLoading : true
        };
        //this.handleChange = this.handleChange.bind(this);
        //this.getNewsList = this.getNewsList.bind(this);
    }

    getNewsList = () =>{
        const urlApi = `https://cosmere-latam.000webhostapp.com/app/index.php?f=newsListAll&sec=${this.state.section}&page=${this.state.page}`;
        console.log(urlApi);
        fetch(urlApi)
            .then(response => response.json())
            .then(data => this.setState({Result : data, isLoading: false}))
            .catch(error => this.setState({ error, isLoading: false }));
    }

    handleChange = (event) =>{
        event.preventDefault();
        console.log(event.target.value);
        this.setState({
            section: event.target.value,
            page: 5,
            Result : [],
            isLoading : true
        });
        //this.getNewsList();
    }

    render(){
        return(
            <>
            <nav className="category">
                <input type="radio" name="category" id="category1" value="all"/>
                <input type="radio" name="category" id="category2" value="news"/>
                <input type="radio" name="category" id="category3" value="event"/>
                <input type="radio" name="category" id="category4" value="adds"/>
                <label  htmlFor="category1">Recientes</label>
                <label  htmlFor="category2">Noticias</label>
                <label  htmlFor="category3">Eventos</label>
                <label  htmlFor="category4">Anuncios</label>
            </nav>
            <div className='newsList'>
                <ul>
                    {!this.state.isLoading ? (
                        this.state.Result.map(result=>(
                            <New
                                key={result.ID}
                                new={result}
                            />
                        ))
                    ) : (
                        this.getNewsList()
                    )}
                    <il className="more">Mas</il>
                </ul>
            </div>
            </>
        )
    }
}
export default News

