import Link from 'next/link';
import {useLocation} from 'next/router';

import './style/navbar.css';

function setSpotlight(url){
    var spot = 'barLine set--';
    var newLoc = url.split("/");
    spot += newLoc[1];
    return(spot);
}

export default function Index(props){
    return(
        <nav className="NavbarItems">

            <span className="nav_logo"></span>

            <input type="checkbox" className="menuBtn" id="menuBtn"/>
            <label className="menuIcon" for="menuBtn">
                <span className="bars"></span>
            </label>

            <ul className="nav_link">
                <li>
                    <Link href="/" >
                        Home
                    </Link>
                </li>
                <li>
                    <Link href="/news" >
                        Noticias
                    </Link>
                </li>
                <li>
                    <Link href="" >
                        Artistas
                    </Link>
                </li>
                <li>
                    <Link href="" >
                        Podcasts
                    </Link>
                </li>
                <li className={setSpotlight(useLocation.pathname)} id="lineNav"></li>
            </ul>
        </nav>
    );
}
